import React, {useState} from "react";

import './Rules.css'

const Rules = () =>{

    const [rulesVisible, setRulesVisible] = useState(false);

    const toggle = () =>{
        setRulesVisible(!rulesVisible)
    };

    return (
        <div id="rules_container">
            <button id="toggle_rules" onClick={toggle}>SÄÄNNÖT</button>
            {rulesVisible &&
                <div id="rules_content">
                    <h3 id="rules_h3">Pelin säännöt:</h3>
                    <p id="rules_p">1. Peliä pelataan klikkaamalla PLAY-nappulaa</p>
                    <p id="rules_p">2. Pelaajalla on pelin alussa 20 pistettä, jokainen painallus maksaa pelaajalle 1 pisteen</p>
                    <p id="rules_p">3. Yksi kierros kestää 3 minuuttia, jonka jälkeen pelaajien
                    pisteet ja kierroslaskuri nollaantuvat. Uusi kierros alkaa automaattisesti vanhan päätyttyä</p>
                    <p id="rules_p">4. </p>
                </div>
            }
        </div>
    )
}

export default Rules;
